public class NestedMultiplicationTables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i;
int j;
for(j=1;j<=10;j++)
{	System.out.printf("Multiplication table for "+j).println();
	for(i=1;i<=10;i++)
			System.out.printf("%d * %d = %d",j,i,j*i).println();
	}}

}